#include<bits/stdc++.h>
using namespace std;

    //number of substrings with atleast one occuerence of each alphabet a,b,c

    //TLE solution
    int numberOfSubstrings(string s) {
        int n=s.length();
        
        int high=0;
        int low=0;
        int count=0;
        for(int i=0;i<n;i++){
            int c1=0,c2=0,c3=0;
            for(int j=i;j<n;j++){
                if(s[j]=='a')
                    c1++;
                else if(s[j]=='b')
                    c2++;
                else if(s[j]=='c')
                    c3++;
                if(c1>=1 && c2>=1 && c3>=1)
                    count++;
            }
        }
        return count;
    }

    int numberOfSubstrings2(string s) {
        int n=s.length();
        int c1=0,c2=0,c3=0;
        int high=0;
        int low=0;
        int count=0;
        int i=0,j=0;
        for( i=0;i<n;i++){
           if(s[i]=='a')
               c1++;
            if(s[i]=='b')
                c2++;
            if(s[i]=='c')
                c3++;
            while(c1>=1 && c2>=1 && c3>=1 && i>=j){
                if(s[j]=='a')
               c1--;
            if(s[j]=='b')
                c2--;
            if(s[j]=='c')
                c3--;
            count+=n-i;
                j++;
            }
            
        }
        return count;
    }

int main(){
    string s="";
    cout<<numberOfSubstrings(s);
}