    bool dfs(vector<vector<int>>&arr, vector<bool>&visited,int curr) {
        bool ans = false;
        visited[curr] = true;
        for(int i=0;i<arr[curr].size();i++){
            if(!visited[arr[curr][i]]){
                ans |= dfs(arr,visited,arr[curr][i]);
            } else {
                ans = true;
            }
        }
        return ans;
    }
